// Helper: Get unique topics
export const getTopics = () => [...new Set(treatiesData.map(t => t.topic))]

// Helper: Get year range
export const getYearRange = () => {
    const years = [...treatiesData, ...casesData, ...resolutionsData].map(item => parseInt(item.date || item.year)).filter(Boolean)
    return { min: Math.min(...years), max: Math.max(...years) }
}
