INSERT INTO treaties (name, date, topic, states, link, description) VALUES
('الميثاق العربي لحقوق الإنسان', '2004', 'حقوق الإنسان', '16', 'https://lasportal.org/ar/humanrights/Committee/Pages/Charter.aspx', 'وثيقة إقليمية محورية تؤكد على مبادئ حقوق الإنسان في الوطن العربي، وتعتبر مكمّلة للمعايير الدولية مع مراعاة الخصوصية الثقافية.'),
('ميثاق جامعة الدول العربية', '1945', 'السلام والأمن', '22', 'https://www.lasportal.org/ar/aboutlas/Pages/Charter.aspx', 'الوثيقة التأسيسية لجامعة الدول العربية، تهدف لتوثيق الصلات بين الدول العربية وصيانة استقلالها وسيادتها.'),
('اتفاقية جنيف الرابعة (حماية المدنيين)', '1949', 'القانون الإنساني', '196', 'https://ihl-databases.icrc.org/ar/ihl-treaties/gciv-1949', 'الاتفاقية الأساسية التي تنظم حماية المدنيين تحت الاحتلال، وهي المرجعية القانونية الأساسية للوضع في الأراضي الفلسطينية المحتلة.'),
('اتفاقية الدفاع العربي المشترك', '1950', 'السلام والأمن', '22', 'http://hrlibrary.umn.edu/arab/a002.html', 'معاهدة للتعاون العسكري والدفاع المشترك بين دول الجامعة العربية، تعتبر أي عدوان على دولة عضو هو عدوان على الجميع.'),
('العهد الدولي الخاص بالحقوق المدنية والسياسية', '1966', 'حقوق الإنسان', '174', 'https://www.ohchr.org/ar/instruments-mechanisms/instruments/international-covenant-civil-and-political-rights', 'انضمام دولة فلسطين لهذه الاتفاقية عام 2014 كان خطوة استراتيجية عززت مكانتها القانونية كدولة ومكنتها من مساءلة الاحتلال.'),
('نظام روما الأساسي (المحكمة الجنائية الدولية)', '1998', 'حقوق الإنسان', '124', 'https://www.ohchr.org/ar/instruments-mechanisms/instruments/rome-statute-international-criminal-court', 'الوثيقة التي مكنت فلسطين (بعد انضمامها في 2015) من طلب التحقيق في جرائم الحرب والجرائم ضد الإنسانية المرتكبة على أراضيها.'),
('اتفاقية لاهاي المتعلقة بقوانين وأعراف الحرب البرية', '1907', 'القانون الإنساني', '—', 'https://ihl-databases.icrc.org/ar/ihl-treaties/hague-conv-iv-1907', 'تعتبر أحكامها عرفاً دولياً ملزماً حتى للدول غير الموقعة، وهي أساسية في تحديد التزامات قوة الاحتلال تجاه الإقليم المحتل.');
INSERT INTO cases (name, year, court, summary, link) VALUES
('الجدار العازل في الأرض الفلسطينية المحتلة (فتوى)', '2004', 'ICJ', 'أكدت محكمة العدل الدولية عدم شرعية بناء الجدار والمستوطنات في الضفة الغربية، وطالبت إسرائيل بالتفكيك والتعويض، ودعت دول العالم لعدم الاعتراف بالوضع غير القانوني.', 'https://www.icj-cij.org/case/131'),
('نقل السفارة الأمريكية إلى القدس (فلسطين ضد الولايات المتحدة)', '2018', 'ICJ', 'دعوى رفعتها دولة فلسطين طعناً في قانونية نقل السفارة الأمريكية إلى القدس، استناداً لاتفاقية فيينا للعلاقات الدبلوماسية التي تحمي الوضع الخاص للمدينة.', 'https://www.icj-cij.org/case/176'),
('الحالة في دولة فلسطين (التحقيق الجنائي)', '2021', 'ICC', 'قرار الدائرة التمهيدية للمحكمة الجنائية الدولية بتأكيد ولايتها القضائية على الأراضي الفلسطينية (الضفة، غزة، القدس الشرقية)، مما فتح الباب للتحقيق في جرائم الحرب.', 'https://www.icc-cpi.int/palestine'),
('تطبيق اتفاقية الإبادة الجماعية (جنوب أفريقيا ضد إسرائيل)', '2024', 'ICJ', 'أمر قضائي تاريخي يطالب إسرائيل باتخاذ تدابير فورية لمنع الإبادة الجماعية في غزة، واعتبار الفلسطينيين "مجموعة محمية" بموجب الاتفاقية، وهو أول إجراء من نوعه ضد إسرائيل.', 'https://www.icj-cij.org/case/192'),
('قضية تعيين الحدود البحرية (قطر ضد البحرين)', '2001', 'ICJ', 'واحدة من أطول القضايا وأكثرها تعقيداً في تاريخ المحكمة، حسمت النزاع الحدود البحري والجزي (جزر حوار) بين الدولتين الشقيقتين بشكل سلمي وقانوني.', 'https://www.icj-cij.org/case/87'),
('الجرف القاري (تونس / الجماهيرية الليبية)', '1982', 'ICJ', 'حكم بارز أسس لمبادئ مهمة في قانون البحار تتعلق بـ "الظروف ذات الصلة" و "الانصاف" في ترسيم الحدود البحرية بين الدول العربية المتجاورة.', 'https://www.icj-cij.org/case/63'),
('أسطول الحرية (مافي مرمرة)', '2013', 'ICC', 'تحقيق يتعلق بالهجوم الإسرائيلي على سفن المساعدات المتجهة لغزة. أقرت المحكمة بوقوع جرائم حرب لكنها ناقشت مسألة "الجاذبية" (Gravity) لفتح قضية كاملة.', 'https://www.icc-cpi.int/comoros');
